import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function SetUrl (urlpath, navigate) {
    fetch("/url", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({url: urlpath}), }) //http request ?url=urlpath
    .then(function (response) {
        return response.json();
    })
    .then(function (data) {
        NumberOfReviews(data, navigate)
    })
    .catch(function (error) {
        console.log(error)
    })
}

function NumberOfReviews(data, navigate) {
    if(data["message"] !== "Collecting data from the US sites.")
    {
        alert(data["message"])
        navigate('/')
    }
    else
    {
        document.getElementById("image-prep").innerText = data["message"]
        fetch("/number-of-reviews")
        .then(function (response) {
            return response.json();
        })
        .then(function (data) {
            getProxies(data)
        })
        .catch(function (error) {
            console.log(error)
        })
    }
}

function getProxies(data) {
    if(data["message"] === "Failed to collect the total number of reviews. Please try again.")
    {
        document.getElementById("image-prep").innerText = data["message"]
    }
    else
    {
        const maxPage = Math.ceil(data["message"]/10.0)
        if (maxPage > 500)
        {
            document.getElementById("image-prep").innerText = data["message"]
            fetch("/get-proxies")
            .then(function (response) {
                return response.json()
            })
            .then(function (data) {
                ScrapeSite(data, proxies)
            })
            .catch(function (error) {
                console.log(error)
            })
        }
        else
        {
            ScrapeSite(data, proxies)
        }
    }
}

function ScrapeSite(data, proxies) {
    if(data["message"] === "Failed to collect the total number of reviews. Please try again.")
    {
        document.getElementById("image-prep").innerText = data["message"]
    }
    else
    {
        const maxPage = Math.ceil(data["message"]/10.0)
        if (maxPage <= 500)
        {
            for(let page = 1; page <= maxPage + 1; page++)
            {
                fetch("/scraper", {
                    method: "POST",
                    headers: {"Content-Type": "application/json"},
                    body: JSON.stringify({"page": page}), }) //http request ?page=(int)page
                .then(function (response) {
                    return response.json()
                })
                .then(function (data) {
                    AccumulateRawData(data, page, maxPage)
                })
                .catch(function (error) {
                    console.log(error)
                })
            }
        }
        else
        {
            let pageCounter = 0
            let pageChunks = Math.ceil(maxPage/900)

            let sec = 0
            var timer = setInterval(function () {
                document.getElementById("image-prep").innerText = "Collecting the reviews, we are about " + ((sec * 100 + 1) / (Math.ceil(pageChunks / 5) * 160)).toFixed(0) + "% done."
                if ((sec + 1) === (Math.ceil(pageChunks / 5) * 160))
                {
                    document.getElementById("image-prep").innerText = "Collecting the reviews, we are about " + (((sec + 1) * 100) / (Math.ceil(pageChunks / 5) * 160)).toFixed(0) + "% done."
                    clearInterval(timer)
                }
                sec++
            }, 250)

            for(let page = 0; page < pageChunks; page++)
            {
                fetch("/scrape-all", {
                    method: "POST",
                    headers: {"Content-Type": "application/json"},
                    body: JSON.stringify({"page-start": (page * 900 + 1), "page-interval": (((maxPage - page * 900) < 900) ? (maxPage - page * 900) : 900)}), })
                .then(function (response) {
                    return response.json();
                })
                .then(function (data) {
                    pageCounter++
                    if (pageCounter === pageChunks)
                        AnalyzeRawData(data, timer)
                })
            }
        }
    }
}

function AccumulateRawData(data, page, maxPage) {
    if(page <= maxPage)
    {
        document.getElementById("image-prep").innerText = "Collecting the reviews, we are about " + ((page * 100) / maxPage).toFixed(0) + "% done."
        // document.getElementById("image-prep").innerText = data["message"]
    }
    else
    {
        fetch("/tutorial-csv")
        .then(function (response) {
            return response.json()
        })
        .then(function (data) {
            AnalyzeRawData(data)
        })
        .catch(function (error) {
            console.log(error)
        })
    }
}

function AnalyzeRawData(data, timer) {
    clearInterval(timer)
    document.getElementById("image-prep").innerText = data["message"]
    fetch("/analyzed-csv")
    .then(function (response) {
        return response.json()
    })
    .then(function (data) {
        document.getElementById("image-prep").innerText = data["message"]
    })
    .catch(function (error) {
        console.log(error)
    })
}

/*
    HOME
    Description: User must insert a valid link. If they do, then it sends request to flask to start creating all the necessary files
*/

export default function Home() {
    const navigate = useNavigate();
    const [content, setContent] = useState("")

    const handleClick = event => {

        const urlpath = String(content);

        // Request from flask to remove the old files
        axios("/remove").catch(error => {
            console.error("Error removing: ", error);
        })

        // placed here to discourage user from spamming the submit button as that would crash the program
        navigate('/analyzer')

        // Sends the request to flask to start creating the files and if an error occurs it is displayed then brings the user back to the home screen
        SetUrl(urlpath, navigate)

    };
    
    return (
        <header class="App-home">
            <h1>ReviewZ</h1>
            <p>
                Get real time feedback on how your product is doing.
            </p>
            <label>
                    Paste your Amazon url below:
            </label>
            <div>
                <input 
                    type="text" 
                    id="url_submit" 
                    value = {content}
                    onChange = {event => setContent(event.target.value)} 
                    placeHolder = "Ex. https://www.amazon.com/Remote-Control-Dancing-Imitates-Animals/dp/B0B6GJ7847/ref=puwl_atf_sccl_1/131-6402069-2006342?pd_rd_w=6XAAv&content-id=amzn1.sym.b8d6fc5b-13fe-440c-8384-29c241919e84&pf_rd_p=b8d6fc5b-13fe-440c-8384-29c241919e84&pf_rd_r=R1HV2HZZKCWCSDP7YDAM&pd_rd_wg=DEZoo&pd_rd_r=c63334db-32ed-4f7e-9a4c-705d0c5c66e6&pd_rd_i=B0B6GJ7847&th=1"
                />
                <button id="search-btn" onClick={handleClick}><span class="iconify-magnify" /></button>
            </div>
        </header>
    );
}
