module.exports = setURL

function setURL (urlpath) {
    fetch("/url", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({url: urlpath}), }) //http request ?url=urlpath
    .then(function (response) {
        return response.json();
    })
    .then(numberOfReviews(data))
    .catch(function (error) {
        console.log(error)
    })
}

function numberOfReviews(data) {
    if(data["message"] !== "Collecting data from the US sites.")
    {
        alert(data["message"])
        navigate('/')
    }
    else
    {
        document.getElementById("image-prep").innerText = data["message"]
        fetch("/number-of-reviews")
        .then(function (response) {
            return response.json();
        })
        .then(scrapeSite(data))
        .catch(function (error) {
            console.log(error)
        })
    }
}

function scrapeSite(data) {
    if(data["message"] === "Failed to collect the total number of reviews. Please try again.")
    {
        document.getElementById("image-prep").innerText = data["message"]
    }
    else
    {
        const maxPage = Math.ceil(data["message"]/10.0)
        alert(maxPage)
        for(let page = 1; page <= maxPage + 1; page++)
        {
            fetch("/scraper", {
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify({"page": page}), }) //http request ?page=(int)page
            .then(function (response) {
                return response.json()
            })
            .then(accumulateRawData(data, page, maxPage))
            .catch(function (error) {
                console.log(error)
            })
        }
    }
}

function accumulateRawData(data, page, maxPage) {
    if(page <= maxPage)
    {
        document.getElementById("image-prep").innerText = "Collecting the reviews, we are about " + ((page * 100) / maxPage).toFixed(0) + "% done."
    }
    else
    {
        fetch("/tutorial-csv")
        .then(function (response) {
            return response.json()
        })
        .then(analyzeRawData(data))
        .catch(function (error) {
            console.log(error)
        })
    }
}

function analyzeRawData(data) {
    document.getElementById("image-prep").innerText = data["message"]
    fetch("/analyzed-csv")
    .then(function (response) {
        return response.json()
    })
    .then(function (data) {
        document.getElementById("image-prep").innerText = data["message"]
    })
    .catch(function (error) {
        console.log(error)
    })
}
